import React from 'react';
import BackgroundImg from '../commons/images/future-medicine.jpg';

import {Button, Form, FormGroup, Label, Input, Container, Jumbotron} from 'reactstrap';
import {HOST} from "../commons/hosts";
import RestApiClient from "../commons/api/rest-client";


const backgroundStyle = {
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    width: "100%",
    height: "1920px",
    backgroundImage: `url(${BackgroundImg})`
};
const textStyle = {
    fontSize: "44px",
    color: 'white',
    textAlign:'center',
    borderBottom: "solid",
    borderWidth: "2px",
    borderColor: "lightgrey",
    padding: "7px"
};

const loginForm = {
    borderRadius: "10px",
    width: "100%",
    maxWidth: "330px",
    padding: "15px",
    margin: "auto",
    height: "23%",
    backgroundColor: "#708090"
};

const button = {
    width: "60%",
    marginTop: "20px",
    marginLeft: "20%",
    height: "35%",
    backgroundColor: "black"
};

const endpoint = {
    person: '/login'
};


class Home extends React.Component {


    render() {

        return (
            <Jumbotron fluid style={backgroundStyle}>
                <Form className="login-form" style={loginForm}>

                    <Container fluid>
                        <h1 className="display-3" style={textStyle}>Login</h1><br/>

                        <FormGroup>
                            <Label>Username</Label>
                            <Input type={"text"}/>
                        </FormGroup>

                        <FormGroup>
                            <Label>Password</Label>
                            <Input type={"password"}/>
                        </FormGroup>
                        <Button style={button} onClick={() => this.checkCredentials()}>Login</Button>
                    </Container>


                </Form>
            </Jumbotron>
        )
    };

    checkCredentials(){
        let request = new Request(HOST.backend_api + endpoint.login, {
            method: 'GET',
        });
        console.log(request.url);
        RestApiClient.performRequest(request, callback);;
    }
}



export default Home
