import React from 'react';
import BackgroundImg from '../commons/images/future-medicine.jpg';

import {Button, Form, FormGroup, Label, Input, Container, Jumbotron} from 'reactstrap';
import {HOST} from "../commons/hosts";
import RestApiClient from "../commons/api/rest-client";
import * as API_USERS from "../person/api/person-api";
import validate from "../person/components/validators/person-validators";


const backgroundStyle = {
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    width: "100%",
    height: "1920px",
    backgroundImage: `url(${BackgroundImg})`
};
const textStyle = {
    fontSize: "44px",
    color: 'white',
    textAlign:'center',
    borderBottom: "solid",
    borderWidth: "2px",
    borderColor: "lightgrey",
    padding: "7px",
    marginBottom:"10px"
};

const loginForm = {
    borderRadius: "10px",
    width: "100%",
    maxWidth: "330px",
    padding: "15px",
    margin: "auto",
    height: "23%",
    backgroundColor: "#708090"
};


const validMsg = {
    textAlign:'center',
    marginBottom: "5px",
    color: "white"
};

const invalidMsg = {
    textAlign:'center',
    marginBottom: "5px",
    color: "red"
};

const button = {
    width: "60%",
    marginTop: "20px",
    marginLeft: "20%",
    height: "35%",
    backgroundColor: "black"
};

const endpoint = {
    login: '/users'
};


class Home extends React.Component {



    constructor(props) {
        super(props);
        this.toggleForm = this.toggleForm.bind(this);


        this.state = {

            errorStatus: 0,
            error: null,


            formControls: {
                username: {
                    value: '',
                    placeholder: '',
                    touched: false,
                },
                password: {
                    value: '',
                    placeholder: '',
                    touched: false
                },
                validMsg: {
                    value: '',
                    placeholder: '',
                    touched: false,
                },
                invalidMsg: {
                    value: '',
                    placeholder: '',
                    touched: false,
                },
            }
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
/*
        this.updateUsername = this.updateUsername.bind(this);
        this.updatePassword = this.updatePassword.bind(this);
        this.updateMsgGood = this.updateMsgGood.bind(this);
        this.updateMsgBad = this.updateMsgBad.bind(this);
        this.onSubmit = this.onSubmit.bind(this);*/
    }


    render() {

        return (
            <Jumbotron fluid style={backgroundStyle}>
                <Form className="login-form" style={loginForm}>

                    <Container fluid>
                        <h1 className="display-3" style={textStyle}>Login</h1>
                        <div className="message" style={validMsg}>{this.state.validMsg}</div>
                        <div className="message" style={invalidMsg}>{this.state.invalidMsg}</div>
                        <FormGroup>
                            <Label>Username</Label>
                            <Input value={this.state.username} onChange={evt => this.updateUsername(evt)} type={"text"} name={"username"}/>
                        </FormGroup>

                        <FormGroup>
                            <Label>Password</Label>
                            <Input value={this.state.password} onChange={evt => this.updatePassword(evt)} type={"password"} id={"password"}/>
                        </FormGroup>
                        <Button style={button} onClick={this.onSubmit}>Login</Button>
                    </Container>


                </Form>
            </Jumbotron>
        )
    };




    handleChange = event => {

        const name = event.target.name;
        const value = event.target.value;

        const updatedControls = this.state.formControls;

        const updatedFormElement = updatedControls[name];

        updatedFormElement.value = value;
        updatedFormElement.touched = true;
        updatedFormElement.valid = validate(value, updatedFormElement.validationRules);
        updatedControls[name] = updatedFormElement;

        let formIsValid = true;
        for (let updatedFormElementName in updatedControls) {
            formIsValid = updatedControls[updatedFormElementName].valid && formIsValid;
        }

        this.setState({
            formControls: updatedControls,
            formIsValid: formIsValid
        });

    };

    validateUser(person) {
        return API_USERS.postLogin(person, (result, status, error) => {
            if (result !== null && (status === 200 || status === 201)) {
                console.log("Login succesfull");
                //this.reloadHandler();
            } else {
                this.setState(({
                    errorStatus: status,
                    error: error
                }));
            }
        });
    }

    handleSubmit() {
        let person = {
            username: this.state.formControls.username.value,
            password: this.state.formControls.password.value,
           };

        console.log(person);
        this.registerPerson(person);
    }
/*
    updateUsername(evt) {
        this.setState({
            username: evt.target.value
        });
    }

    updatePassword(evt) {
        this.setState({
            password: evt.target.value
        });
    }

    updateMsgGood(evt) {
        this.setState({
            validMsg: "Login successfull!",
            invalidMsg: ""
        });
    }

    updateMsgBad(evt) {
        this.setState({
            invalidMsg: "Invalid username or password!",
            validMsg: ""
        });
    }

    onSubmit(){
        let login = {
            username: this.state.formControls.username.value,
            password: this.state.formControls.password.value
        };
        this.checkLogin(login);
    }


    checkLogin(login){

        return this.sendRequest(login, (result, status, err) => {
            console.log(status);
            console.log("Result = " + result);
            if (result !== null && (status === 200 || status === 201)) {
                this.updateMsgGood();
            } else {
                console.log("There was an error " + err);
                this.updateMsgBad();
            }
        });
    }

    sendRequest(login, callback){
        let request = new Request(HOST.backend_api + endpoint.login + "/login", {
            method: 'POST',
            headers : {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(login)
        });
        console.log(request.url);
        console.log(login);
        RestApiClient.performRequest(request, callback, login);
    }*/
}



export default Home
