function performRequest(request, callback){

    fetch(request)
        .then(
            function(response) {
                if (response.ok) {
                    console.log(response.status);
                    response.then(json => callback(json, response.status,null));
                }
                else {
                    response.then(err => callback(null, response.status,  err));
                }
            })
        .catch(function (err) {
            //catch any other unexpected error, and set custom code for error = 1
            callback(null, 1, err)
        });

}
module.exports = {
    performRequest
};
