import React from 'react';
import validate from "../../patient/components/validators/patient-validators";
import Button from "react-bootstrap/Button";
import * as API_USERS from "../../patient/api/patient-api";
import APIResponseErrorMessage from "../../commons/errorhandling/api-response-error-message";
import {Col, Row} from "reactstrap";
import { FormGroup, Input, Label} from 'reactstrap';



class PatientForm extends React.Component {

    constructor(props) {
        super(props);
        this.toggleForm = this.toggleForm.bind(this);
        this.reloadHandler = this.props.reloadHandler;

        this.state = {

            errorStatus: 0,
            error: null,

            formIsValid: false,

            formControls: {
                name: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
                gender: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
                date: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
                address: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
                record: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
                caregiver: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
                medplan: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },

            }
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    toggleForm() {
        this.setState({collapseForm: !this.state.collapseForm});
    }


    handleChange = event => {

        const name = event.target.name;
        const value = event.target.value;

        const updatedControls = this.state.formControls;

        const updatedFormElement = updatedControls[name];

        updatedFormElement.value = value;
        updatedFormElement.touched = true;
        updatedFormElement.valid = validate(value, updatedFormElement.validationRules);
        updatedControls[name] = updatedFormElement;

        let formIsValid = true;
        for (let updatedFormElementName in updatedControls) {
            formIsValid = updatedControls[updatedFormElementName].valid && formIsValid;
        }

        this.setState({
            formControls: updatedControls,
            formIsValid: formIsValid
        });

    };

    registerPatient(patient) {
        return API_USERS.postPatient(patient, (result, status, error) => {
            if (result !== null && (status === 200 || status === 201)) {
                console.log("Successfully inserted patient with id: " + result);
                //this.reloadHandler();
            } else {
                this.setState(({
                    errorStatus: status,
                    error: error
                }));
            }
        });
    }

    registerPatient(name) {
        return API_USERS.getCaregiverByName(name, (result, status, error) => {
            if (result !== null && (status === 200 || status === 201)) {
                console.log("Successfully found caregiver with id: " + result);

                //this.reloadHandler();
            } else {
                this.setState(({
                    errorStatus: status,
                    error: error
                }));
            }
        });
    }

    handleSubmit() {
        let caregiverId;
        let patient = {
            name: this.state.formControls.name.value,
            gender: this.state.formControls.gender.value,
            date: this.state.formControls.date.value,
            address: this.state.formControls.address.value,
            record: this.state.formControls.record.value,
            caregiver: this.state.formControls.caregiver.value,
            medplan: this.state.formControls.medplan.value
        };

        console.log(patient);
        this.registerPatient(patient);
    }

    render() {
        return (
            <div>

                <FormGroup id='name'>
                    <Label for='nameField'> Name: </Label>
                    <Input name='name' id='nameField' placeholder={this.state.formControls.name.placeholder}
                           onChange={this.handleChange}
                           defaultValue={this.state.formControls.name.value}
                           touched={this.state.formControls.name.touched? 1 : 0}
                           required
                    />
                </FormGroup>

                <FormGroup id='gender'>
                    <Label for='genderField'> Gender: </Label>
                    <Input name='gender' id='genderField' placeholder={this.state.formControls.gender.placeholder}
                           onChange={this.handleChange}
                           defaultValue={this.state.formControls.gender.value}
                           touched={this.state.formControls.gender.touched? 1 : 0}
                           required
                    />
                </FormGroup>

                <FormGroup id='date'>
                    <Label for='dateField'> Birthday: </Label>
                    <Input name='date' id='dateField' placeholder={this.state.formControls.date.placeholder}
                           type="date"
                           onChange={this.handleChange}
                           defaultValue={this.state.formControls.date.value}
                           touched={this.state.formControls.date.touched? 1 : 0}
                           required
                    />
                </FormGroup>

                <FormGroup id='address'>
                    <Label for='addressField'> Address: </Label>
                    <Input name='address' id='addressField' placeholder={this.state.formControls.address.placeholder}
                           onChange={this.handleChange}
                           defaultValue={this.state.formControls.address.value}
                           touched={this.state.formControls.address.touched? 1 : 0}
                           required
                    />
                </FormGroup>

                <FormGroup id='record'>
                    <Label for='recordField'> Record: </Label>
                    <Input name='record' id='recordField' placeholder={this.state.formControls.record.placeholder}
                           onChange={this.handleChange}
                           defaultValue={this.state.formControls.record.value}
                           touched={this.state.formControls.record.touched? 1 : 0}
                           required
                    />
                </FormGroup>

                <FormGroup id='caregiver'>
                    <Label for='caregiverField'> Caregiver: </Label>
                    <Input name='caregiver' id='caregiverField' placeholder={this.state.formControls.caregiver.placeholder}
                           onChange={this.handleChange}
                           defaultValue={this.state.formControls.caregiver.value}
                           touched={this.state.formControls.caregiver.touched? 1 : 0}
                           required
                    />
                </FormGroup>

                <FormGroup id='medplan'>
                    <Label for='medplanField'> Medication Plan: </Label>
                    <Input name='medplan' id='medplanField' placeholder={this.state.formControls.medplan.placeholder}
                           onChange={this.handleChange}
                           defaultValue={this.state.formControls.medplan.value}
                           touched={this.state.formControls.medplan.touched? 1 : 0}
                           required
                    />
                </FormGroup>


                    <Row>
                        <Col sm={{size: '4', offset: 8}}>
                            <Button type={"submit"} onClick={this.handleSubmit}>  Submit </Button>
                        </Col>
                    </Row>
            </div>
        ) ;
    }
}

export default PatientForm;
