import {HOST} from '../../commons/hosts';
import RestApiClient from "../../commons/api/rest-client";


const endpoint = {
    patient: '/patient'
};

function getPatients(callback) {
    let request = new Request(HOST.backend_api + endpoint.patient, {
        method: 'GET',
    });
    console.log(request.url);
    RestApiClient.performRequest(request, callback);
}

function getPatientById(params, callback){
    let request = new Request(HOST.backend_api + endpoint.patient + params.id, {
       method: 'GET'
    });

    console.log(request.url);
    RestApiClient.performRequest(request, callback);
}

function postPatient(patient, callback){
    let request = new Request(HOST.backend_api + endpoint.patient + "/save", {
        method: 'POST',
        headers : {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(patient)
    });

    console.log("URL: " + request.url);
    RestApiClient.performRequest(request, callback);
}

function getCaregiverByName(caregiver, callback){
    let name= "";
    for(let i = 0; i<caregiver.length;i++) {
        if(caregiver.charAt(i) === ' ')
            name += '-';
        else
            name += caregiver.charAt(i);

    }
    console.log("Sending " + name);
    let request = new Request(HOST.backend_api + "/caregiver/" + name, {
        method: 'POST',
        headers : {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(name)
    });

    RestApiClient.performRequest(request, callback);

}

export {
    getPatients,
    getPatientById,
    postPatient,
    getCaregiverByName
};
