import React from 'react';

import * as API_USERS from "./login-api";

import {Button, Form, Label, Input, Jumbotron, NavLink} from 'reactstrap';

import BackgroundImg from "../commons/images/future-medicine.jpg";
import Redirect from "react-router-dom";

const backgroundStyle = {
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    width: "100%",
    height: "1920px",
    backgroundImage: `url(${BackgroundImg})`
};

const textStyle = {
    fontSize: "44px",
    color: 'white',
    textAlign:'center',
    borderBottom: "solid",
    borderWidth: "2px",
    borderColor: "lightgrey",
    padding: "7px",
    marginBottom:"10px"
};

const loginForm = {
    borderRadius: "10px",
    width: "100%",
    maxWidth: "330px",
    padding: "15px",
    margin: "auto",
    height: "23%",
    backgroundColor: "#708090"
};


const validMsg = {
    textAlign:'center',
    marginBottom: "5px",
    color: "white"
};

const invalidMsg = {
    textAlign:'center',
    marginBottom: "5px",
    color: "red"
};

const button = {
    width: "60%",
    marginTop: "40px",
    marginLeft: "20%",
    height: "15%",
    backgroundColor: "black"
};

class LoginForm extends React.Component {

    constructor(props) {
        super(props);
        this.toggleForm = this.toggleForm.bind(this);
        //this.reloadHandler = this.props.reloadHandler;

        this.state = {

            errorStatus: 0,
            error: null,
            validMsg: '',
            invalidMsg: '',

            formControls: {
                username: {
                    value: '',
                    placeholder: '',
                    //valid: false,
                    touched: false,
                },
                password: {
                    value: '',
                    placeholder: '',
                    //valid: false,
                    touched: false,
                },
            }
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    toggleForm() {
        this.setState({collapseForm: !this.state.collapseForm});
    }


    handleChange = event => {

        const name = event.target.name;
        const value = event.target.value;

        const updatedControls = this.state.formControls;

        const updatedFormElement = updatedControls[name];

        updatedFormElement.value = value;
        updatedFormElement.touched = true;

        updatedControls[name] = updatedFormElement;

        let formIsValid = true;
        for (let updatedFormElementName in updatedControls) {
            formIsValid = updatedControls[updatedFormElementName].valid && formIsValid;
        }

        this.setState({
            formControls: updatedControls,
            formIsValid: true
        });

    };

    verifyLogin(login) {
        return API_USERS.postLogin(login, (result, status, error) => {
            if (result !== null && (status === 200 || status === 201)) {

                this.setState({
                    validMsg: "Login successful!",
                    invalidMsg: ""
                });
                //this.reloadHandler();
                switch (result.role){
                    case 0: {
                        localStorage.setItem("role", "0");
                        break;
                    }
                    case 1: {
                        localStorage.setItem("role", "1");
                        break;
                    }
                    case 2: {
                        localStorage.setItem("role", "2");
                        break;
                    }
                }

            } else {

                this.setState(({
                    errorStatus: status,
                    error: error,
                    invalidMsg: "Invalid username or password!",
                    validMsg: ""
                }));
            }
        });
    }

    handleSubmit() {
       /* let login = {
            username: this.state.formControls.username.value,
            password: this.state.formControls.password.value
        };

        console.log(login);
        this.verifyLogin(login);*/
        return(
            <Redirect to="/caregiver"/>);   
    }

    render() {
        localStorage.setItem("role", "42");
        return (
            <div>
                <Jumbotron style={backgroundStyle}>
                    <Form style={loginForm}>
                        <h1 style={textStyle}>Login</h1>
                        <div className="message" style={validMsg}>{this.state.validMsg}</div>
                        <div className="message" style={invalidMsg}>{this.state.invalidMsg}</div>

                        <Label htmlFor='username'> Username: </Label>
                        <Input type='text' name='username' id='username'
                               onChange={this.handleChange}
                               touched={this.state.formControls.username.touched? 1 : 0}
                               required
                        />


                        <Label htmlFor='password'> Password: </Label>
                        <Input name='password' id='password' type='password'
                               onChange={this.handleChange}
                               touched={this.state.formControls.password.touched? 1 : 0}
                               required
                        />

                        <Button style={button} onClick={this.handleSubmit}> Login </Button>

                    </Form>


                </Jumbotron>
            </div>
        ) ;
    }
}

export default LoginForm;
