import React from 'react';

import * as API_USERS from "./login-api";

import {Button, Form, FormGroup, Label, Input, Container, Jumbotron, Row, Col} from 'reactstrap';

import BackgroundImg from "../commons/images/future-medicine.jpg";


const backgroundStyle = {
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    width: "100%",
    height: "1920px",
    backgroundImage: `url(${BackgroundImg})`
};
const textStyle = {
    fontSize: "44px",
    color: 'white',
    textAlign:'center',
    borderBottom: "solid",
    borderWidth: "2px",
    borderColor: "lightgrey",
    padding: "7px",
    marginBottom:"10px"
};

const loginForm = {
    borderRadius: "10px",
    width: "100%",
    maxWidth: "330px",
    padding: "15px",
    margin: "auto",
    height: "23%",
    backgroundColor: "#708090"
};


const validMsg = {
    textAlign:'center',
    marginBottom: "5px",
    color: "white"
};

const invalidMsg = {
    textAlign:'center',
    marginBottom: "5px",
    color: "red"
};

const button = {
    width: "60%",
    marginTop: "40px",
    marginLeft: "20%",
    height: "15%",
    backgroundColor: "black"
};

class LoginForm extends React.Component {

    constructor(props) {
        super(props);
        this.toggleForm = this.toggleForm.bind(this);
        this.reloadHandler = this.props.reloadHandler;

        this.state = {

            errorStatus: 0,
            error: null,

            formIsValid: false,

            formControls: {
                username: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
                password: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
                validMsg: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
                invalidMsg: {
                    value: '',
                    placeholder: '',
                    valid: false,
                    touched: false,
                },
            }
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    toggleForm() {
        this.setState({collapseForm: !this.state.collapseForm});
    }


    handleChange = event => {

        console.log("CHANGE");
        const name = event.target.name;
        const value = event.target.value;

        const updatedControls = this.state.formControls;

        const updatedFormElement = updatedControls[name];

        updatedFormElement.value = value;
        updatedFormElement.touched = true;

        updatedControls[name] = updatedFormElement;

        let formIsValid = true;
        for (let updatedFormElementName in updatedControls) {
            formIsValid = updatedControls[updatedFormElementName].valid && formIsValid;
        }

        this.setState({
            formControls: updatedControls,
            formIsValid: formIsValid
        });

    };

    registerPerson(person) {
        return API_USERS.postPerson(person, (result, status, error) => {
            if (result !== null && (status === 200 || status === 201)) {
                console.log("Successfully inserted person with id: " + result);
                this.reloadHandler();
            } else {
                this.setState(({
                    errorStatus: status,
                    error: error
                }));
            }
        });
    }

    handleSubmit() {
        let person = {
            name: this.state.formControls.name.value,
            email: this.state.formControls.email.value,
            age: this.state.formControls.age.value,
            address: this.state.formControls.address.value
        };

        console.log(person);
        this.registerPerson(person);
    }

    render() {
        return (
            <div>
                <Jumbotron fluid style={backgroundStyle}>
                    <Form fluid style={loginForm}>
                        <h1 fluid style={textStyle}>Login</h1>
                        <FormGroup id='name'>
                            <Label for='nameField'> Username: </Label>
                            <Input name='name' id='nameField'
                                   onChange={this.handleChange}
                                   defaultValue={''}
                                   touched={this.state.formControls.name.touched? 1 : 0}
                                   required
                            />

                        </FormGroup>

                        <FormGroup id='pass'>
                            <Label for='passField'> Password: </Label>
                            <Input name='pass' id='passField' type='password'
                                   onChange={this.handleChange}
                                   defaultValue={''}
                                   touched={this.state.formControls.email.touched? 1 : 0}
                                   required
                            />
                        </FormGroup>

                        <Button fluid style={button} type={"submit"} disabled={!this.state.formIsValid} onClick={this.handleSubmit}>  Login </Button>

                    </Form>


                </Jumbotron>
            </div>
        ) ;
    }
}

export default LoginForm;
