import React from 'react';
import BackgroundImg from '../commons/images/future-medicine.jpg';

import {Button, Form, FormGroup, Label, Input, Container, Jumbotron} from 'reactstrap';
import {HOST} from "../commons/hosts";
import RestApiClient from "../commons/api/rest-client";
import * as API_USERS from "../person/api/person-api";


const backgroundStyle = {
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    width: "100%",
    height: "1920px",
    backgroundImage: `url(${BackgroundImg})`
};
const textStyle = {
    fontSize: "44px",
    color: 'white',
    textAlign:'center',
    borderBottom: "solid",
    borderWidth: "2px",
    borderColor: "lightgrey",
    padding: "7px"
};

const loginForm = {
    borderRadius: "10px",
    width: "100%",
    maxWidth: "330px",
    padding: "15px",
    margin: "auto",
    height: "23%",
    backgroundColor: "#708090"
};

const button = {
    width: "60%",
    marginTop: "20px",
    marginLeft: "20%",
    height: "35%",
    backgroundColor: "black"
};

const endpoint = {
    login: '/users'
};


class Home extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: ''
        };
        this.updateUsername = this.updateUsername.bind(this);
        this.updatePassword = this.updatePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }



    render() {

        return (
            <Jumbotron fluid style={backgroundStyle}>
                <Form className="login-form" style={loginForm}>

                    <Container fluid>
                        <h1 className="display-3" style={textStyle}>Login</h1><br/>

                        <FormGroup>
                            <Label>Username</Label>
                            <Input value={this.state.username} onChange={evt => this.updateUsername(evt)} type={"text"} name={"username"}/>
                        </FormGroup>

                        <FormGroup>
                            <Label>Password</Label>
                            <Input value={this.state.password} onChange={evt => this.updatePassword(evt)} type={"password"} id={"password"}/>
                        </FormGroup>
                        <Button style={button} onClick={this.onSubmit}>Login</Button>
                    </Container>


                </Form>
            </Jumbotron>
        )
    };

    updateUsername(evt) {
        this.setState({
            username: evt.target.value
        });
    }

    updatePassword(evt) {
        this.setState({
            password: evt.target.value
        });
    }


    onSubmit(){
        let login = {
            username: this.state.username,
            password: this.state.password
        };
        this.checkLogin(login);
    }


    checkLogin(login){

        return this.sendRequest(login, (result, status, err) => {
            console.log(status);
            if (result !== null && (status === 200 || status === 201)) {
                console.log("Successfully inserted person with id: " + result);
            } else {
                console.log("There was an error " + err);
            }
        });
    }

    sendRequest(login, callback){
        let request = new Request(HOST.backend_api + endpoint.login + "/login", {
            method: 'POST',
            headers : {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(login)
        });
        console.log(request.url);
        console.log(login);
        RestApiClient.performRequest(request, callback, login);
    }
}



export default Home
